import React, { useState, useEffect } from "react";

const MobileStore = () => {
    const [mobiles, setMobiles] = useState([]);
    const [search, setSearch] = useState("");

    useEffect(() => {
        fetch(`http://localhost:5000/api/mobiles?search=${search}`)
            .then((response) => response.json())
            .then((data) => {
                console.log(data); // Debugging: Check if data is being fetched correctly
                setMobiles(data.data || []); // Update the state with the fetched data
            })
            .catch((error) => console.error("Error fetching mobiles:", error));
    }, [search]);

    return (
        <div style={{ padding: "20px" }}>
            <h1>MOBILE STORE</h1>
            <input
                type="text"
                placeholder="Search for a mobile..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
            />
            <button onClick={() => setSearch(search)}>Search</button>
            <div>
                {mobiles.length === 0 ? (
                    <p>No phones found</p>
                ) : (
                    <div>
                        {mobiles.map((mobile) => (
                            <div key={mobile.id} style={{ margin: "20px" }}>
                                <img
                                    src={mobile.image_url}
                                    alt={mobile.name}
                                    style={{ width: "150px", height: "150px" }}
                                />
                                <h3>{mobile.name}</h3>
                                <p>Price: ${mobile.price}</p>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default MobileStore;
