import React, { useState } from 'react';
import PropTypes from 'prop-types';

const BookingForm = ({ show, onClose, onSubmit, title = "Book Your Phone" }) => {
    const [formData, setFormData] = useState({
        name: '',
        address: '',
        deliveryDate: '',
        mobile_id: '',  // Added mobile_id for selecting the phone
    });
    const [errors, setErrors] = useState({});
    const [isLoading, setIsLoading] = useState(false);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
        setErrors({ ...errors, [e.target.name]: '' }); // Clear error on change
    };

    const validateForm = () => {
        const newErrors = {};
        if (!formData.name.trim()) newErrors.name = 'Name is required.';
        if (!formData.address.trim()) newErrors.address = 'Address is required.';
        if (!formData.deliveryDate) newErrors.deliveryDate = 'Delivery date is required.';
        if (!formData.mobile_id) newErrors.mobile_id = 'Please select a phone.';
        return newErrors;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newErrors = validateForm();
        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
            return;
        }

        setIsLoading(true);
        try {
            const result = await onSubmit(formData); // Call the onSubmit prop function
            if (result?.success) {
                alert('Booking successful!');
                setFormData({ name: '', address: '', deliveryDate: '', mobile_id: '' }); // Reset form
                onClose(); // Close the modal
            } else {
                const message = result?.message || 'Failed to book the phone. Please try again.';
                throw new Error(message);
            }
        } catch (error) {
            alert(`Error booking phone: ${error.message}`);
        } finally {
            setIsLoading(false);
        }
    };

    if (!show) return null; // Don't render the form if it's not visible

    return (
        <div className="modal" style={{ display: 'block', backgroundColor: 'rgba(0, 0, 0, 0.5)' }}>
            <div className="modal-dialog modal-dialog-centered">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title">{title}</h5>
                        <button type="button" className="btn-close" onClick={onClose} aria-label="Close"></button>
                    </div>
                    <div className="modal-body">
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <label htmlFor="name" className="form-label">Name</label>
                                <input
                                    type="text"
                                    id="name"
                                    name="name"
                                    className={`form-control ${errors.name ? 'is-invalid' : ''}`}
                                    placeholder="Enter your name"
                                    value={formData.name}
                                    onChange={handleChange}
                                    required
                                />
                                {errors.name && <div className="invalid-feedback">{errors.name}</div>}
                            </div>
                            <div className="mb-3">
                                <label htmlFor="address" className="form-label">Address</label>
                                <textarea
                                    id="address"
                                    name="address"
                                    className={`form-control ${errors.address ? 'is-invalid' : ''}`}
                                    placeholder="Enter your address"
                                    value={formData.address}
                                    onChange={handleChange}
                                    required
                                ></textarea>
                                {errors.address && <div className="invalid-feedback">{errors.address}</div>}
                            </div>
                            <div className="mb-3">
                                <label htmlFor="deliveryDate" className="form-label">Delivery Date</label>
                                <input
                                    type="date"
                                    id="deliveryDate"
                                    name="deliveryDate"
                                    className={`form-control ${errors.deliveryDate ? 'is-invalid' : ''}`}
                                    value={formData.deliveryDate}
                                    onChange={handleChange}
                                    required
                                />
                                {errors.deliveryDate && <div className="invalid-feedback">{errors.deliveryDate}</div>}
                            </div>
                            <div className="mb-3">
                                <label htmlFor="mobile_id" className="form-label">Select Mobile</label>
                                <select
                                    id="mobile_id"
                                    name="mobile_id"
                                    className={`form-control ${errors.mobile_id ? 'is-invalid' : ''}`}
                                    value={formData.mobile_id}
                                    onChange={handleChange}
                                    required
                                >
                                    <option value="">Select a phone</option>
                                    {/* You can dynamically populate this list from available phones */}
                                    <option value="1">Phone 1</option>
                                    <option value="2">Phone 2</option>
                                    {/* Add more options here */}
                                </select>
                                {errors.mobile_id && <div className="invalid-feedback">{errors.mobile_id}</div>}
                            </div>
                            <div className="d-grid">
                                <button
                                    type="submit"
                                    className="btn btn-success"
                                    disabled={isLoading}
                                >
                                    {isLoading ? 'Submitting...' : 'Submit'}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

BookingForm.propTypes = {
    show: PropTypes.bool.isRequired,
    onClose: PropTypes.func.isRequired,
    onSubmit: PropTypes.func.isRequired,
    title: PropTypes.string,
};

export default BookingForm;
