import React, { useState, useEffect } from 'react';
import SearchBar from './components/SearchBar';
import MobileCard from './components/MobileCard';
import BookingForm from './components/BookingForm';

const App = () => {
  const [mobiles, setMobiles] = useState([]);
  const [filteredMobiles, setFilteredMobiles] = useState([]);
  const [showBookingForm, setShowBookingForm] = useState(false);

  useEffect(() => {
    // Fetch mobile data from backend
    fetch('/api/mobiles')
      .then((response) => response.json())
      .then((data) => {
        setMobiles(data);
        setFilteredMobiles(data);
      });
  }, []);

  const handleSearch = (query) => {
    const filtered = mobiles.filter((mobile) =>
      mobile.name.toLowerCase().includes(query)
    );
    setFilteredMobiles(filtered);
  };

  const handleBookingSubmit = (bookingData) => {
    // Send booking data to backend
    fetch('/api/book', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(bookingData),
    })
      .then((response) => response.json())
      .then(() => {
        alert('Your phone is booked successfully!');
        setShowBookingForm(false);
      });
  };

  return (
    <div className="container">
      <h1 className="text-center my-4">Mobile Store</h1>
      <SearchBar onSearch={handleSearch} />
      <div className="row">
        {filteredMobiles.map((mobile) => (
          <MobileCard key={mobile.id} mobile={mobile} />
        ))}
      </div>
      <button
        type="button"
        className="btn btn-primary w-100 mt-4"
        data-toggle="modal"
        data-target="#bookingModal"
      >
        Book Now
      </button>
      {showBookingForm && <BookingForm onSubmit={handleBookingSubmit} />}
    </div>
  );
};

export default App;
