import React, { useState } from 'react';
import BookingForm from './components/BookingForm';

const App = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredMobiles, setFilteredMobiles] = useState(mobileData); // Replace `mobileData` with your mobile phone data array
    const [isBookingFormVisible, setIsBookingFormVisible] = useState(false);

    const mobileData = [
        { id: 1, name: 'iPhone 14', price: '₹799.99', image: 'iphone14.png' },
        { id: 2, name: 'Samsung Galaxy S23', price: '₹699.99', image: 'galaxyS23.png' },
        { id: 3, name: 'Google Pixel 8', price: '₹599.99', image: 'pixel8.png' },
        // Add more mobile data here
    ];

    const handleSearch = () => {
        const filtered = mobileData.filter((mobile) =>
            mobile.name.toLowerCase().includes(searchTerm.toLowerCase())
        );
        setFilteredMobiles(filtered);
    };

    return (
        <div className="container">
            <h1 className="text-center my-4">Mobile Store</h1>
            <div className="d-flex justify-content-center mb-4">
                <input
                    type="text"
                    className="form-control w-50"
                    placeholder="Search for mobiles..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button className="btn btn-primary ml-2" onClick={handleSearch}>
                    Search
                </button>
            </div>
            <div className="row">
                {filteredMobiles.map((mobile) => (
                    <div className="col-md-4 mb-4" key={mobile.id}>
                        <div className="card">
                            <img
                                src={mobile.image}
                                alt={mobile.name}
                                className="card-img-top"
                                style={{ height: '200px', objectFit: 'cover' }}
                            />
                            <div className="card-body">
                                <h5 className="card-title">{mobile.name}</h5>
                                <p className="card-text">Price: {mobile.price}</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
            <div className="text-center mt-4">
                <button
                    className="btn btn-success"
                    onClick={() => setIsBookingFormVisible(true)}
                >
                    Book Now
                </button>
            </div>
            <BookingForm
                show={isBookingFormVisible}
                onClose={() => setIsBookingFormVisible(false)}
                title="Book Your Phone"
            />
        </div>
    );
};

export default App;
