import React, { useEffect, useState } from 'react';

const MobileCard = () => {
  const [mobiles, setMobiles] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetch('/api/mobiles')
      .then((response) => response.json())
      .then((data) => {
        if (data.message === 'No phones found') {
          setMessage('No phones found');
        } else {
          setMobiles(data);
        }
      })
      .catch((error) => {
        console.error('Error fetching mobile data:', error);
        setMessage('Failed to fetch mobile data');
      });
  }, []);

  return (
    <div>
      {message && <p>{message}</p>}
      <div className="mobile-list">
        {mobiles.length > 0 ? (
          mobiles.map((mobile) => (
            <div key={mobile.id} className="mobile-card">
              <img src={mobile.image_url} alt={mobile.name} />
              <h3>{mobile.name}</h3>
              <p>{mobile.details}</p>
              <p>Price: ${mobile.price}</p>
            </div>
          ))
        ) : (
          <p>No mobiles available</p>
        )}
      </div>
    </div>
  );
};

export default MobileCard;
