import React, { useState, useEffect } from 'react';
import SearchBar from './components/SearchBar';
import MobileCard from './components/MobileCard';
import BookingForm from './components/BookingForm';

const App = () => {
    const [mobiles, setMobiles] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedMobile, setSelectedMobile] = useState(null); // Track selected mobile for booking

    // Fetch all mobile data when the app loads
    useEffect(() => {
        fetchMobiles();
    }, []);

    const fetchMobiles = async (query = '') => {
        setLoading(true);
        setError('');
        const endpoint = query
            ? `http://127.0.0.1:8000/api/phones?search=${query}`
            : `http://127.0.0.1:8000/api/phones`;

        try {
            const response = await fetch(endpoint);
            if (!response.ok) {
                throw new Error('Failed to fetch data from the server');
            }
            const data = await response.json();
            if (data.message === 'No phones found') {
                setMobiles([]);
                setError('No phones found');
            } else {
                setMobiles(data);
            }
        } catch (err) {
            console.error('Error fetching mobiles:', err);
            setError('Failed to fetch data. Please check your connection or try again later.');
        } finally {
            setLoading(false);
        }
    };

    const handleSearch = (query) => {
        fetchMobiles(query);
    };

    const handleBooking = async (bookingDetails) => {
        try {
            const response = await fetch('http://127.0.0.1:8000/api/book', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    ...bookingDetails,
                    phoneId: selectedMobile?.id, // Include selected mobile ID in booking
                }),
            });

            if (response.ok) {
                alert('Your phone is booked successfully!');
                setShowForm(false);
                setSelectedMobile(null); // Clear selected mobile after successful booking
            } else {
                throw new Error('Failed to book the phone');
            }
        } catch (err) {
            console.error('Error booking phone:', err);
            alert('Error booking phone. Please try again later.');
        }
    };

    const handleBookNowClick = (mobile) => {
        setSelectedMobile(mobile);
        setShowForm(true);
    };

    return (
        <div className="container mt-4">
            <h1 className="text-center">MOBILE STORE</h1>
            <SearchBar onSearch={handleSearch} />
            <div className="row mt-4">
                {loading ? (
                    <div className="col-12">Loading...</div>
                ) : error ? (
                    <div className="col-12 text-danger">{error}</div>
                ) : mobiles.length > 0 ? (
                    mobiles.map((mobile) => (
                        <div key={mobile.id} className="col-md-4 mb-4">
                            <MobileCard 
                                mobile={mobile} 
                                onBookNow={() => handleBookNowClick(mobile)} 
                            />
                        </div>
                    ))
                ) : (
                    <div className="col-12">No phones found</div>
                )}
            </div>
            <BookingForm
                show={showForm}
                onClose={() => {
                    setShowForm(false);
                    setSelectedMobile(null);
                }}
                onSubmit={handleBooking}
                title={`Book Your Phone${selectedMobile ? `: ${selectedMobile.name}` : ''}`}
            />
        </div>
    );
};

export default App;
