import React, { useState } from 'react';
import MobileCard from './MobileCard';
import BookingForm from './BookingForm';

const App = () => {
    const [showForm, setShowForm] = useState(false);

    const mobiles = [
        { name: 'iPhone 14', image_url: 'iphone14.jpg', details: '128GB, Midnight', price: 799.99 },
        { name: 'Samsung Galaxy S23', image_url: 'samsungS23.jpg', details: '256GB, Phantom Black', price: 699.99 },
        { name: 'Google Pixel 8', image_url: 'pixel8.jpg', details: '128GB, Obsidian', price: 599.99 },
        { name: 'OnePlus 11', image_url: 'oneplus11.jpg', details: '256GB, Titan Black', price: 699.99 },
    ];

    const handleBookNow = () => setShowForm(true);
    const handleCloseForm = () => setShowForm(false);

    return (
        <div className="container text-center mt-4">
            <h1 className="mb-4">MOBILE STORE</h1>
            <input
                type="text"
                placeholder="Search for mobiles..."
                className="form-control mb-4"
                style={{ maxWidth: '400px', margin: '0 auto' }}
            />
            <div className="row">
                {mobiles.map((mobile, index) => (
                    <div className="col-md-4 mb-4" key={index}>
                        <MobileCard mobile={mobile} />
                    </div>
                ))}
            </div>
            {/* Single Book Now Button */}
            <button
                className="btn btn-primary mt-3"
                style={{ padding: '10px 20px', fontSize: '16px' }}
                onClick={handleBookNow}
            >
                Book Now
            </button>
            <BookingForm show={showForm} onClose={handleCloseForm} onSubmit={(data) => console.log(data)} />
        </div>
    );
};

export default App;
