import React, { useState } from 'react';
import axios from 'axios';

const SearchBar = ({ onSearch }) => {
    const [query, setQuery] = useState('');

    const handleSearch = async () => {
        if (query.trim()) {
            try {
                // Sending search query to the backend
                const response = await axios.get(`http://127.0.0.1:5000/search?q=${query.trim()}`);
                if (response.data.message === 'No phones found') {
                    onSearch([]);  // Pass empty array to parent if no results
                } else {
                    onSearch(response.data);  // Pass the fetched data to parent
                }
            } catch (err) {
                console.error('Error fetching data:', err);
                alert('Error fetching phones. Please try again later.');
            }
        }
    };

    return (
        <div className="input-group">
            <input
                type="text"
                className="form-control"
                placeholder="Search for mobiles..."
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSearch()} // Handle Enter key press
            />
            <button className="btn btn-primary" onClick={handleSearch}>
                Search
            </button>
        </div>
    );
};

export default SearchBar;
