import React from 'react';
import PropTypes from 'prop-types';

const MobileCard = ({ mobile, onBookNow }) => {
    return (
        <div className="card shadow-sm" style={{ width: "18rem", margin: "10px" }}>
            <img
                src={mobile.image_url || "https://via.placeholder.com/200"}
                alt={mobile.name || "Mobile"}
                className="card-img-top"
                style={{ height: '200px', objectFit: 'cover' }}
            />
            <div className="card-body">
                <h5 className="card-title">{mobile.name || "Unknown Mobile"}</h5>
                <p className="card-text text-muted" style={{ fontSize: '14px' }}>
                    {mobile.details || "No details available."}
                </p>
                <p className="card-text font-weight-bold">
                    Price: â‚¹{mobile.price ? mobile.price.toLocaleString('en-IN', { minimumFractionDigits: 2 }) : "N/A"}
                </p>
                <button
                    className="btn btn-primary w-100"
                    onClick={() => onBookNow(mobile.id)}
                >
                    Book Now
                </button>
            </div>
        </div>
    );
};

MobileCard.propTypes = {
    mobile: PropTypes.shape({
        name: PropTypes.string,
        image_url: PropTypes.string,
        details: PropTypes.string,
        price: PropTypes.number,
    }).isRequired,
    onBookNow: PropTypes.func.isRequired,
};

export default MobileCard;
