import React, { useState, useEffect } from 'react';
import SearchBar from './components/SearchBar';
import MobileCard from './components/MobileCard';
import BookingForm from './components/BookingForm';

const App = () => {
    const [mobiles, setMobiles] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // Fetching all mobile data when the app loads
    useEffect(() => {
        fetchMobiles();
    }, []);

    const fetchMobiles = (query = '') => {
        setLoading(true);
        setError('');
        const endpoint = query
            ? `http://127.0.0.1:8000/api/mobiles?search=${query}`
            : `http://127.0.0.1:8000/api/mobiles`;
        
        fetch(endpoint)
            .then((res) => {
                if (!res.ok) {
                    throw new Error('Failed to fetch data from the server');
                }
                return res.json();
            })
            .then((data) => setMobiles(data))
            .catch((error) => setError(error.message))
            .finally(() => setLoading(false));
    };

    const handleSearch = (query) => {
        fetchMobiles(query);
    };

    const handleBooking = (bookingDetails) => {
        fetch('http://127.0.0.1:8000/api/book', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(bookingDetails),
        })
            .then((res) => {
                if (res.ok) {
                    alert('Your phone is booked successfully!');
                    setShowForm(false);
                } else {
                    throw new Error('Failed to book the phone');
                }
            })
            .catch((error) => console.error('Error booking phone:', error));
    };

    return (
        <div className="container mt-4">
            <h1 className="text-center">MOBILE STORE</h1>
            <SearchBar onSearch={handleSearch} />
            <div className="row mt-4">
                {loading ? (
                    <div className="col-12">Loading...</div>
                ) : error ? (
                    <div className="col-12 text-danger">{error}</div>
                ) : mobiles.length > 0 ? (
                    mobiles.map((mobile) => (
                        <div key={mobile.id} className="col-md-4 mb-4">
                            <MobileCard mobile={mobile} />
                        </div>
                    ))
                ) : (
                    <div className="col-12">No phones found</div>
                )}
            </div>
            <div className="text-center mt-4">
                <button className="btn btn-primary" onClick={() => setShowForm(true)}>Book Now</button>
            </div>
            <BookingForm show={showForm} onClose={() => setShowForm(false)} onSubmit={handleBooking} />
        </div>
    );
};

export default App;
