import React, { useState, useEffect } from 'react';
import SearchBar from './components/SearchBar';
import MobileCard from './components/MobileCard';
import BookingForm from './components/BookingForm';

const App = () => {
    const [mobiles, setMobiles] = useState([]);
    const [showForm, setShowForm] = useState(false);

    // Fetching all mobile data when the app loads
    useEffect(() => {
        fetch('/api/mobiles')
            .then((res) => res.json())
            .then((data) => setMobiles(data))
            .catch((error) => console.error('Error fetching mobile data:', error));
    }, []);

    const handleSearch = (query) => {
        fetch(`/api/mobiles?search=${query}`)
            .then((res) => res.json())
            .then((data) => setMobiles(data))
            .catch((error) => console.error('Error fetching searched mobiles:', error));
    };

    const handleBooking = (bookingDetails) => {
        fetch('/api/book', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(bookingDetails),
        }).then((res) => {
            if (res.ok) {
                alert('Your phone is booked successfully!');
                setShowForm(false);
            }
        }).catch((error) => console.error('Error booking phone:', error));
    };

    return (
        <div className="container mt-4">
            <h1 className="text-center">MOBILE STORE</h1>
            <SearchBar onSearch={handleSearch} />
            <div className="row mt-4">
                {mobiles.length > 0 ? (
                    mobiles.map((mobile) => (
                        <div key={mobile.id} className="col-md-4 mb-4">
                            <MobileCard mobile={mobile} />
                        </div>
                    ))
                ) : (
                    <div className="col-12">No phones found</div>
                )}
            </div>
            <div className="text-center mt-4">
                <button className="btn btn-primary" onClick={() => setShowForm(true)}>Book Now</button>
            </div>
            <BookingForm show={showForm} onClose={() => setShowForm(false)} onSubmit={handleBooking} />
        </div>
    );
};

export default App;
