import React from 'react';
import PropTypes from 'prop-types'; // Add prop-types for type-checking

const MobileCard = ({ mobile }) => {
    return (
        <div className="card shadow-sm" style={{ width: "18rem", margin: "10px" }}>
            {/* Display a fallback if image_url is missing */}
            <img
                src={mobile.image_url || "https://via.placeholder.com/200"}
                alt={mobile.name}
                className="card-img-top"
                style={{ height: '200px', objectFit: 'cover' }}
            />
            <div className="card-body">
                <h5 className="card-title">{mobile.name}</h5>
                <p className="card-text text-muted" style={{ fontSize: '14px' }}>
                    {mobile.details || "No details available."}
                </p>
                <p className="card-text font-weight-bold">
                    Price: ${mobile.price ? mobile.price.toFixed(2) : "N/A"}
                </p>
            </div>
        </div>
    );
};

// Add PropTypes for validation
MobileCard.propTypes = {
    mobile: PropTypes.shape({
        name: PropTypes.string.isRequired,
        image_url: PropTypes.string,
        details: PropTypes.string,
        price: PropTypes.number,
    }).isRequired,
};

export default MobileCard;
