import React, { useState, useEffect } from 'react';
import SearchBar from './components/SearchBar';
import MobileCard from './components/MobileCard';
import BookingForm from './components/BookingForm';

const App = () => {
    const [mobiles, setMobiles] = useState([]);
    const [filteredMobiles, setFilteredMobiles] = useState([]);
    const [selectedMobile, setSelectedMobile] = useState(null);

    useEffect(() => {
        // Fetch mobile data from backend
        fetch('/api/mobiles')
            .then((response) => response.json())
            .then((data) => {
                setMobiles(data);
                setFilteredMobiles(data);
            });
    }, []);

    const handleSearch = (query) => {
        const lowercasedQuery = query.toLowerCase();
        const results = mobiles.filter((mobile) =>
            mobile.name.toLowerCase().includes(lowercasedQuery)
        );
        setFilteredMobiles(results);
    };

    const handleBookNow = (mobile) => {
        setSelectedMobile(mobile); // Open booking form
    };

    const handleBookingSubmit = (formData) => {
        // Submit booking form data
        fetch('/api/book', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData),
        })
            .then((response) => response.json())
            .then((data) => {
                alert('Your phone is booked successfully!');
                setSelectedMobile(null); // Close the form
            });
    };

    return (
        <div className="container">
            <h1 className="text-center my-4">MOBILE STORE</h1>
            <SearchBar onSearch={handleSearch} />
            <div className="row mt-4">
                {filteredMobiles.map((mobile) => (
                    <div className="col-md-4" key={mobile.id}>
                        <MobileCard mobile={mobile} onBook={handleBookNow} />
                    </div>
                ))}
            </div>
            {selectedMobile && (
                <BookingForm
                    mobile={selectedMobile}
                    onSubmit={handleBookingSubmit}
                    onClose={() => setSelectedMobile(null)}
                />
            )}
        </div>
    );
};

export default App;
