import React, { useState } from 'react';

const BookingForm = ({ phoneName }) => {
    const [name, setName] = useState('');
    const [mobileNumber, setMobileNumber] = useState('');
    const [address, setAddress] = useState('');
    const [deliveryDate, setDeliveryDate] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        const bookingData = {
            name: name,
            phone_name: phoneName,
            mobile_number: mobileNumber,
            address: address,
            delivery_date: deliveryDate,
        };

        try {
            const response = await fetch('http://127.0.0.1:8000/api/book', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(bookingData),
            });

            const result = await response.json();
            if (response.ok) {
                setMessage(result.message);  // Success message
            } else {
                setMessage(result.message);  // Error message
            }
        } catch (error) {
            console.error('Error booking phone:', error);
            setMessage('Error booking phone. Please try again later.');
        }
    };

    return (
        <div className="modal-body">
            <h5>Book {phoneName}</h5>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Your Name</label>
                    <input
                        type="text"
                        className="form-control"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="mobileNumber" className="form-label">Mobile Number</label>
                    <input
                        type="text"
                        className="form-control"
                        id="mobileNumber"
                        value={mobileNumber}
                        onChange={(e) => setMobileNumber(e.target.value)}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="address" className="form-label">Address</label>
                    <textarea
                        className="form-control"
                        id="address"
                        rows="3"
                        value={address}
                        onChange={(e) => setAddress(e.target.value)}
                        required
                    ></textarea>
                </div>
                <div className="mb-3">
                    <label htmlFor="deliveryDate" className="form-label">Delivery Date</label>
                    <input
                        type="date"
                        className="form-control"
                        id="deliveryDate"
                        value={deliveryDate}
                        onChange={(e) => setDeliveryDate(e.target.value)}
                        required
                    />
                </div>
                <button type="submit" className="btn btn-primary">Book Now</button>
            </form>
            {message && <div className="alert alert-info mt-3">{message}</div>}
        </div>
    );
};

export default BookingForm;
