import React, { useState, useEffect } from 'react';
import SearchBar from './components/SearchBar';
import MobileCard from './components/MobileCard';
import BookingForm from './components/BookingForm';

const App = () => {
    const [mobiles, setMobiles] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedMobile, setSelectedMobile] = useState(null);

    // Fetch all mobile data when the app loads
    useEffect(() => {
        fetchMobiles(); // Fetch all phones initially
    }, []);

    const fetchMobiles = async () => {
        setLoading(true);
        setError('');
        try {
            const response = await fetch('http://127.0.0.1:5000/search'); // Get all phones without a query
            if (!response.ok) {
                throw new Error('Failed to fetch data from the server');
            }
            const data = await response.json();
            setMobiles(data); // Populate the mobiles list with fetched data
        } catch (err) {
            console.error('Error fetching mobiles:', err);
            setError('Failed to fetch data. Please check your connection or try again later.');
        } finally {
            setLoading(false);
        }
    };

    const handleSearch = (searchResults) => {
        setMobiles(searchResults); // Update the mobiles state with search results
    };

    const handleBooking = async (bookingDetails) => {
        try {
            const response = await fetch('http://127.0.0.1:5000/api/book', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    ...bookingDetails,
                    phoneId: selectedMobile?.id, // Include selected mobile ID in booking
                }),
            });

            if (response.ok) {
                alert('Your phone is booked successfully!');
                setShowForm(false);
                setSelectedMobile(null);
            } else {
                throw new Error('Failed to book the phone');
            }
        } catch (err) {
            console.error('Error booking phone:', err);
            alert('Error booking phone. Please try again later.');
        }
    };

    const handleBookNowClick = (mobile) => {
        setSelectedMobile(mobile); // Set the selected mobile for booking
        setShowForm(true); // Show the booking form
    };

    return (
        <div className="container mt-4">
            <h1 className="text-center">MOBILE STORE</h1>
            <SearchBar onSearch={handleSearch} />
            <div className="row mt-4">
                {loading ? (
                    <div className="col-12">Loading...</div>
                ) : error ? (
                    <div className="col-12 text-danger">{error}</div>
                ) : mobiles.length > 0 ? (
                    mobiles.map((mobile) => (
                        <div key={mobile.id} className="col-md-4 mb-4">
                            <MobileCard 
                                mobile={mobile} 
                                onBookNow={() => handleBookNowClick(mobile)} 
                            />
                        </div>
                    ))
                ) : (
                    <div className="col-12">No phones found</div>
                )}
            </div>
            <div className="text-center mt-4">
                <button
                    className="btn btn-primary"
                    onClick={() => {
                        setSelectedMobile(null); // No specific phone selected for this booking
                        setShowForm(true); // Show form for general booking
                    }}
                >
                    Book Now
                </button>
            </div>
            <BookingForm
                show={showForm}
                onClose={() => {
                    setShowForm(false);
                    setSelectedMobile(null);
                }}
                onSubmit={handleBooking}
                title={`Book Your Phone${selectedMobile ? `: ${selectedMobile.name}` : ''}`}
            />
        </div>
    );
};

export default App;
